frappe.ui.form.on('Request for Quotation', {
    refresh(frm) {
        // Supplier filter in Suppliers child table
        frm.set_query('supplier', 'suppliers', function(doc, cdt, cdn) {
            return {
                filters: {
                    company: doc.company
                }
            };
        });

        // Item Code filter in Items child table
        frm.fields_dict['items'].grid.get_field('item_code').get_query = function(doc, cdt, cdn) {
            const supplier = doc.suppliers?.[0]?.supplier || "";

            return {
                query: "erpnext.controllers.queries.item_query",
                filters: {
                    is_purchase_item: 1,
                    supplier: supplier,
                    company: doc.company
                }
            };
        };
    }
});

